CREATE TABLE TOURNAMENT ("ID" INTEGER not null primary key, "ID_ASSOCIATION" INTEGER not null, "TYPE_ASSOCIATION" VARCHAR(3) not null, "LOCATION" VARCHAR(50) not null, "NAME" VARCHAR(50) not null, "YEAR" INTEGER not null, "SURFACE" VARCHAR(50) not null, "BEST_OF_SET" INTEGER not null)
CREATE TABLE PLAYER("ID" INTEGER not null primary key, "NAME" VARCHAR(50) not null, "SEXE" CHAR(1) not null)
CREATE TABLE MATCH("ID" INTEGER not null primary key, "P1_SETS" INTEGER, "P1_SET1" INTEGER, "P1_SET2" INTEGER, "P1_SET3" INTEGER, "P1_SET4" INTEGER, "P1_SET5" INTEGER, "P2_SETS" INTEGER, "P2_SET1" INTEGER, "P2_SET2" INTEGER, "P2_SET3" INTEGER, "P2_SET4" INTEGER, "P2_SET5" INTEGER, "ROUND" VARCHAR(50) not null, "STATE" VARCHAR(50) not null, "COMMENT" VARCHAR(50), "WINNER" INTEGER, "LOSER" INTEGER, "PLAYER1_ID" INTEGER not null, "PLAYER2_ID" INTEGER not null, "TOURNAMENT_ID" INTEGER not null)
ALTER TABLE MATCH ADD CONSTRAINT MATCH_P1_FK FOREIGN KEY ("PLAYER1_ID") REFERENCES PLAYER ("ID")
ALTER TABLE MATCH ADD CONSTRAINT MATCH_P2_FK FOREIGN KEY ("PLAYER2_ID") REFERENCES PLAYER ("ID")
ALTER TABLE MATCH ADD CONSTRAINT TOURNAMENT_FK FOREIGN KEY ("TOURNAMENT_ID") REFERENCES TOURNAMENT ("ID")
